import React, { useState, useEffect } from 'react';

function App() {
  const [gameState, setGameState] = useState('start'); // 'start', 'playing', 'roundEnd'
  const [currentRound, setCurrentRound] = useState(1);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [currentEmotion, setCurrentEmotion] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [options, setOptions] = useState([]);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const emotions = ['happy', 'sad', 'angry', 'surprised', 'neutral'];
  const questionsPerRound = 5;

  // Generate random options including the correct answer
  const generateOptions = (correctEmotion) => {
    const otherEmotions = emotions.filter(emotion => emotion !== correctEmotion);
    const shuffledOthers = otherEmotions.sort(() => Math.random() - 0.5).slice(0, 4);
    const allOptions = [...shuffledOthers, correctEmotion];
    return allOptions.sort(() => Math.random() - 0.5);
  };

  // Start a new round
  const startRound = () => {
    setGameState('playing');
    setCurrentQuestion(0);
    setScore(0);
    setSelectedAnswer(null);
    setIsCorrect(null);
    setError('');
    generateNewQuestion();
  };

  // Generate a new question
  const generateNewQuestion = async () => {
    setIsLoading(true);
    setError('');

    try {
      // Randomly select an emotion
      const randomEmotion = emotions[Math.floor(Math.random() * emotions.length)];
      setCurrentEmotion(randomEmotion);

      // Fetch image from API
      const imageResponse = await fetch(`http://nanalab.ai:8080/get_image/${randomEmotion}`);
      if (!imageResponse.ok) {
        throw new Error('Failed to fetch image');
      }

      // For demo purposes, we'll use a placeholder image since the API might not be available
      // In production, you would use: setImageUrl(imageResponse.url);
      setImageUrl(`https://via.placeholder.com/400x300/0ea5e9/ffffff?text=${randomEmotion}`);

      // Generate options
      const questionOptions = generateOptions(randomEmotion);
      setOptions(questionOptions);

    } catch (err) {
      setError('Failed to load image. Please try again.');
      console.error('Error generating question:', err);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle answer selection
  const handleAnswerSelect = (selectedEmotion) => {
    if (selectedAnswer !== null) return; // Prevent multiple selections

    setSelectedAnswer(selectedEmotion);
    const correct = selectedEmotion === currentEmotion;
    setIsCorrect(correct);

    if (correct) {
      setScore(prev => prev + 1);
    }
  };

  // Move to next question
  const nextQuestion = () => {
    const nextQuestionIndex = currentQuestion + 1;

    if (nextQuestionIndex >= questionsPerRound) {
      // Round finished
      endRound();
    } else {
      // Move to next question
      setCurrentQuestion(nextQuestionIndex);
      setSelectedAnswer(null);
      setIsCorrect(null);
      generateNewQuestion();
    }
  };

  // End the round
  const endRound = async () => {
    setGameState('roundEnd');

    // Save score to backend
    try {
      const response = await fetch('/save_score', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          round: currentRound,
          score: score
        })
      });

      if (!response.ok) {
        console.error('Failed to save score');
      }
    } catch (err) {
      console.error('Error saving score:', err);
    }
  };

  // Start a new round
  const startNewRound = () => {
    setCurrentRound(prev => prev + 1);
    startRound();
  };

  // Render start screen
  if (gameState === 'start') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="bg-white rounded-lg shadow-lg p-8 max-w-md w-full mx-4">
          <h1 className="text-3xl font-bold text-center text-gray-800 mb-6">
            üòä Emotion Recognition Game
          </h1>
          <p className="text-gray-600 text-center mb-8">
            Practice identifying emotions with AI-generated images!
          </p>
          <button
            onClick={startRound}
            className="w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 px-6 rounded-lg transition-colors"
          >
            Start Round
          </button>
        </div>
      </div>
    );
  }

  // Render round end screen
  if (gameState === 'roundEnd') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="bg-white rounded-lg shadow-lg p-8 max-w-md w-full mx-4">
          <h1 className="text-3xl font-bold text-center text-gray-800 mb-6">
            Round Complete! üéâ
          </h1>
          <div className="text-center mb-8">
            <p className="text-2xl font-bold text-blue-600 mb-2">
              Score: {score}/{questionsPerRound}
            </p>
            <p className="text-gray-600">
              Round {currentRound}
            </p>
          </div>
          <button
            onClick={startNewRound}
            className="w-full bg-green-500 hover:bg-green-600 text-white font-semibold py-3 px-6 rounded-lg transition-colors"
          >
            Start New Round
          </button>
        </div>
      </div>
    );
  }

  // Render game screen
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8">
      <div className="max-w-4xl mx-auto px-4">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <div className="flex justify-between items-center">
            <h1 className="text-2xl font-bold text-gray-800">
              Emotion Recognition Game
            </h1>
            <div className="text-right">
              <p className="text-sm text-gray-600">Round {currentRound}</p>
              <p className="text-lg font-semibold text-blue-600">
                Question {currentQuestion + 1}/{questionsPerRound}
              </p>
              <p className="text-sm text-gray-600">Score: {score}</p>
            </div>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-6">
            {error}
          </div>
        )}

        {/* Image Display */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-4 text-center">
            What emotion is this person showing?
          </h2>

          {isLoading ? (
            <div className="flex justify-center items-center h-64">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
            </div>
          ) : (
            <div className="flex justify-center">
              <img
                src={imageUrl}
                alt="Emotion to identify"
                className="max-w-full h-64 object-contain rounded-lg border-2 border-gray-200"
              />
            </div>
          )}
        </div>

        {/* Answer Options */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {options.map((emotion, index) => (
              <button
                key={index}
                onClick={() => handleAnswerSelect(emotion)}
                disabled={selectedAnswer !== null || isLoading}
                className={`
                  p-4 rounded-lg font-semibold transition-all
                  ${selectedAnswer === null
                    ? 'bg-gray-100 hover:bg-gray-200 text-gray-800'
                    : selectedAnswer === emotion
                    ? emotion === currentEmotion
                      ? 'bg-green-500 text-white'
                      : 'bg-red-500 text-white'
                    : emotion === currentEmotion
                    ? 'bg-green-500 text-white'
                    : 'bg-gray-100 text-gray-600'
                  }
                  ${selectedAnswer !== null ? 'cursor-default' : 'cursor-pointer'}
                `}
              >
                {emotion.charAt(0).toUpperCase() + emotion.slice(1)}
              </button>
            ))}
          </div>

          {/* Feedback */}
          {selectedAnswer !== null && (
            <div className="mt-6 text-center">
              {isCorrect ? (
                <p className="text-green-600 font-semibold text-lg">‚úÖ Correct!</p>
              ) : (
                <p className="text-red-600 font-semibold text-lg">
                  ‚ùå Incorrect. The correct answer was: {currentEmotion.charAt(0).toUpperCase() + currentEmotion.slice(1)}
                </p>
              )}

              <button
                onClick={nextQuestion}
                className="mt-4 bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-6 rounded-lg transition-colors"
              >
                {currentQuestion + 1 >= questionsPerRound ? 'Finish Round' : 'Next Question'}
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
